$(function() {
    $(".custom-menu-primary").addClass("js-enabled");
    $(".custom-menu-primary .hs-menu-wrapper").before('<div class="mobile-trigger"><i></i></div>');
    $(".custom-menu-primary .flyouts .hs-item-has-children > a").after(' <div class="child-trigger"><i></i></div>');
    $(".mobile-trigger").click(function() {
        $(this).next(".custom-menu-primary .hs-menu-wrapper").slideToggle(250);
        $("body").toggleClass("mobile-open");
        $(".child-trigger").removeClass("child-open");
        $(".hs-menu-children-wrapper").slideUp(250);
        return false
    });
    $(".child-trigger").click(function() {
        $(this).parent().siblings(".hs-item-has-children").find(".child-trigger").removeClass("child-open");
        $(this).parent().siblings(".hs-item-has-children").find(".hs-menu-children-wrapper").slideUp(250);
        $(this).next(".hs-menu-children-wrapper").slideToggle(250);
        $(this).next(".hs-menu-children-wrapper").children(".hs-item-has-children").find(".hs-menu-children-wrapper").slideUp(250);
        $(this).next(".hs-menu-children-wrapper").children(".hs-item-has-children").find(".child-trigger").removeClass("child-open");
        $(this).toggleClass("child-open");
        return false
    });
    $(".footer-container-wrapper").after('<div class="overlay"></div>');
    $(".custom-menu-primary .hs-menu-wrapper > ul > li").each(function() {
        var c = $(this).children("a").text().replace(/\s/g, "-").toLowerCase();
        $(this).addClass(c)
    });
    //   $(".hs-button.primary").wrapAll('<div class="form-button-layer"></div>');
    $(".hs-testimonial-slider .slick-prev").detach().insertAfter(".hs-testimonial-slider .hs_cos_gallery_main .slick-list");
    $(document).ready(function() {
        $("a[href*=#]:not([href=#])").click(function() {
            if (location.pathname.replace(/^\//, "") == this.pathname.replace(/^\//, "") && location.hostname == this.hostname) {
                var c = $(this.hash);
                c = c.length ? c : $("[name=" + this.hash.slice(1) + "]");
                if (c.length) {
                    $("html,body").animate({
                        scrollTop: c.offset().top - 0
                    }, 1200)
                }
                return false
            }
        })
    });
    $(window).load(function() {
        var c = $(window.location.hash);
        if (c.length) {
            $("html,body").animate({
                scrollTop: c.offset().top - 0
            }, 1000)
        }
    });
    var b = $("select#selectCountry option + option");
    var a = b.map(function(c, d) {
        return {
            t: $(d).text(),
            v: d.value
        }
    }).get();
    a.sort(function(d, c) {
        return d.t > c.t ? 1 : d.t < c.t ? -1 : 0
    });
    b.each(function(c, d) {
        // console.log(c);
        d.value = a[c].v;
        $(d).text(a[c].t)
    })
});